import { Knex } from "knex";
import { countItemsInDB } from "./count";
import { findAllIemsNoPopulate } from "./find";
import { IfilterFind, IfilterPopulate } from "./interfaces";
import { findOneItem } from "./findOne";
import { insertItemIntoDatabase } from "./insert";
import { findAndJoinTableFetch } from "./populate";
import { updateItemSinDatabase } from "./update";

export class Pipeline {
	public dbconnection;
	public module;

	constructor(dbconnection: Knex, module: string) {
		this.dbconnection = dbconnection;
		this.module = module;
	}

	async count(
		filterWith: Record<string, any>[] | Record<string, any>,
		table: string,
		filterWithout?: Record<string, any> | Record<string, any>[]
	) {
		// initiating count steps
		return await countItemsInDB(
			this.dbconnection,
			filterWith,
			table,
			filterWithout
		);
	}

	async find(options: IfilterFind, needsPagination: boolean) {
		return await findAllIemsNoPopulate(
			options,
			this.dbconnection,
			needsPagination
		);
	}

	async findOne(
		filterWith: Record<string, any>[] | Record<string, any>,
		table: string,
		col?: string[],
		filterWithout?: Record<string, any> | Record<string, any>[]
	) {
		col = col?.length ? col : [];
		return await findOneItem(
			filterWith,
			table,
			col,
			this.dbconnection,
			filterWithout
		);
	}

	async insert(
		data: Record<string, any> | Record<string, any>[],
		table: string
	) {
		return await insertItemIntoDatabase(data, table, this.dbconnection);
	}

	async populate(options: IfilterPopulate, needsPagination: boolean) {
		return await findAndJoinTableFetch(
			options,
			needsPagination,
			this.dbconnection
		);
	}

	async update(
		data: Record<string, any> | Record<string, any>[],
		filterWith: Record<string, any>,
		table: string
	) {
		return await updateItemSinDatabase(
			data,
			table,
			filterWith,
			this.dbconnection
		);
	}
}

import { Knex } from "knex";

export const countItemsInDB = async (
	dbconnection: Knex,
	filterWith: Record<string, any>[] | Record<string, any>, // can be an array for or values OR and for AND values
	table: string,
	filterWithout: Record<string, any> | Record<string, any>[] = {}
) => {
	let query = dbconnection(table).count();

	if (Array.isArray(filterWith)) {
		filterWith.forEach((filter: Record<string, any>) => {
			query = query.orWhere(filter);
		});
	} else {
		query = query.where(filterWith);
	}

	// filtering out elements
	if (Array.isArray(filterWithout)) {
		filterWithout.forEach((filter: Record<string, any>) => {
			query = query.orWhereNot(filter);
		});
	} else if (filterWithout) {
		query = query.whereNot(filterWithout);
	}

	const count = await query;

	return { count };
};

import { Knex } from "knex";
import { IfilterFind } from "./interfaces";

export const findAllIemsNoPopulate = async (
	options: IfilterFind,
	dbconnection: Knex,
	needsPagination: boolean
) => {
	options.cols = options.cols ? options.cols : [];
	options.offset = options.offset ? options.offset : 0;
	options.limit = options.limit ? options.limit : 0;
	options.aggregations = options.aggregations ? options.aggregations : [];

	let query = dbconnection.select(options.cols).from(options.table);

	if (Array.isArray(options.filterWith)) {
		options.filterWith.forEach((filter: Record<string, any>) => {
			query = query.orWhere(filter);
		});
	} else {
		query = query.where(options.filterWith);
	}

	if (Array.isArray(options.filterWithout)) {
		options.filterWithout.forEach((filter: Record<string, any>) => {
			query = query.orWhereNot(filter);
		});
	} else if (options.filterWithout) {
		query = query.whereNot(options.filterWithout);
	}

	if (options.searchWord) {
		query = query.andWhere((qb: Knex) => {
			qb.where((innerQB: Knex) => {
				options.searchkeys.forEach((searchKey, index) => {
					if (index === 0) {
						innerQB.where(searchKey, "like", `%${options.searchWord}%`);
					} else {
						innerQB.orWhere(searchKey, "like", `%${options.searchWord}%`);
					}
				});
			});
		});
	}

	if (options.cols.length > 0) {
		options.cols.map((column: string) => {
			if (/\b as \b/g.test(column)) {
				const regex = new RegExp(`\\b${"as"}\\b`, "g");
				column = column.split(regex)[0].trim();
			}

			return column;
		});

		query = query.groupBy(options.cols);
	}

	if (options.timeFilters) {
		query = query.whereBetween(options.timeFilters.key, [
			options.timeFilters.fromTime,
			options.timeFilters.toTime
		]);
	}

	if (options.aggregations.length > 0) {
		options.aggregations.forEach((agg) => {
			const raw = `${agg.aggType}(${agg.col}) as ${agg.alias}`;

			query = query.select(dbconnection.raw(raw));
		});
	}

	if (needsPagination) {
		const result = await query
			.orderBy(options.orderBy.col, options.orderBy.order)
			.offset(options.offset)
			.limit(options.limit);

		const count = await query.count("* as count");

		return { count: count || 0, result };
	}
	const result = await query.orderBy(
		options.orderBy.col,
		options.orderBy.order
	);

	return { result };
};

import { Knex } from "knex";

export const findOneItem = async (
	filterWith: Record<string, any> | Record<string, any>[],
	table: string,
	col: string[],
	dbconnection: Knex,
	filterWithout?: Record<string, any> | Record<string, any>[]
) => {
	let query = dbconnection(table).first(col);

	if (Array.isArray(filterWith)) {
		filterWith.forEach((filter: Record<string, any>) => {
			query = query.orWhere(filter);
		});
	} else {
		query = query.where(filterWith);
	}

	// filtering out elements
	if (Array.isArray(filterWithout)) {
		filterWithout.forEach((filter: Record<string, any>) => {
			query = query.orWhereNot(filter);
		});
	} else if (filterWithout) {
		query = query.whereNot(filterWithout);
	}

	return await query;
};

import { Knex } from "knex";

export const insertItemIntoDatabase = async (
	data: Record<string, any> | Record<string, any>[],
	table: string,
	dbconnection: Knex
) => {
	await dbconnection(table).insert(data);

	return { ...data, created_at: new Date(), updated_at: new Date() };
};

import { Knex } from "knex";
import { IfilterPopulate } from "./interfaces";

export const findAndJoinTableFetch = async (
	options: IfilterPopulate,
	needsPagination: boolean,
	dbconnection: Knex
) => {
	options.foreignKeys = options.foreignKeys ? options.foreignKeys : [];
	options.offset = options.offset ? options.offset : 0;
	options.limit = options.limit ? options.limit : 0;
	options.aggregations = options.aggregations ? options.aggregations : [];
	options.groupBy = options.groupBy ? options.groupBy : [];

	if (!options.tableNames || options.tableNames.length < 1) {
		return { count: 0, result: [] };
	}

	const primaryTble = options.tableNames[0];

	let query = dbconnection.select(options.cols).from(primaryTble);

	for (let Joiner of options.foreignKeys) {
		query = query.join(
			Joiner.joinTable,
			`${primaryTble}.${Joiner.foreignKey}`,
			"=",
			`${Joiner.joinTable}.${Joiner.localKey}`
		);
	}

	if (Array.isArray(options.filterWith)) {
		options.filterWith.forEach((filter: Record<string, any>) => {
			query = query.orWhere(filter);
		});
	} else {
		query = query.where(options.filterWith);
	}

	// filtering out elements
	if (Array.isArray(options.filterWithout)) {
		options.filterWithout.forEach((filter: Record<string, any>) => {
			query = query.orWhereNot(filter);
		});
	} else if (options.filterWithout) {
		query = query.whereNot(options.filterWithout);
	}

	if (options.filterRaw?.length) {
		options.filterRaw.forEach((rawText) => {
			query.whereRaw(rawText);
		});
	}

	if (options.searchWord) {
		query = query.andWhere((qb) => {
			qb.where((innerQB) => {
				options.searchkeys.forEach((searchKey, index) => {
					if (index === 0) {
						innerQB.where(searchKey, "like", `%${options.searchWord}%`);
					} else {
						innerQB.orWhere(searchKey, "like", `%${options.searchWord}%`);
					}
				});
			});
		});
	}

	if (options.timeFilters?.key) {
		query = query.whereBetween(options.timeFilters.key, [
			options.timeFilters.fromTime,
			options.timeFilters.toTime
		]);
	}

	// Aggregations
	if (options.aggregations.length > 0) {
		options.aggregations.forEach((agg) => {
			const raw = `${agg.aggType}(${agg.col}) as ${agg.alias}`;

			query = query.select(dbconnection.raw(raw));
		});
	}

	if (options.groupBy.length) {
		query = query.groupBy(options.groupBy);
	}

	if (needsPagination) {
		const queryClone = query.clone();

		const count = await queryClone.count("* as count");

		const result = await query
			.orderBy(options.orderBy.col, options.orderBy.order)
			.offset(options.offset)
			.limit(options.limit);

		return { count: count.length || 0, result };
	} else {
		const result = await query.orderBy(
			options.orderBy.col,
			options.orderBy.order
		);

		return { result };
	}
};

import { Knex } from "knex";

export const updateItemSinDatabase = async (
	data: Record<string, any> | Record<string, any>[],
	table: string,
	filterWith: Record<string, any>,
	dbconnections: Knex
) => {
	const updateData = await dbconnections(table).update(data).where(filterWith);

	return { updateData, data, updated_at: new Date() };
};
